import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Titles from "./components/Titles.js";
import Tournaments from "./components/Tournaments.js";

const Username = "fdolsky@gmail.com"
const Password = "cc03e747a6afbbcbf8be7668acfebee5"
const User = "johancrypton"

class App extends Component {
  getTournaments = async () => {
    const api_call = await fetch(`https://www.sharkscope.com/poker-statistics/networks/PokerStars/players/${User}/activeTournaments?Username=${Username}&Password=${Password}}`,
    {method: 'get', headers: new Headers({'Accept': 'application/json'})});
    const data = await api_call.xml();
  }
  render() {
    return (
      <div className="App">
      <Titles />
      <Tournaments getTournaments={this.getTournaments}/>
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Just Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>

      </div>
    );
  }
}

export default App;
